import cv2

class DENSE:
    def __init__(self, step=6, radius=.5):
        self.step = step
        self.radius = radius

    def detect(self, img):
        kps = []

        for x in range(0, img.shape[1], self.step):
            for y in range(0, img.shape[0], self.step):
                kps.append(cv2.KeyPoint(x, y, self.radius))
        return kps

    def setInt(self, var, val):
        if var == "initXyStep":
            self.step = val
